import React, { useState, useEffect } from 'react';

export default function Users() {
  const [user, setUser] = useState({
    username: "",
    job: ""
  });
  const [users, setUsers] = useState([]);

  const handleChange = (e) => { 
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
  };

  const addUser = () => {
    localStorage.setItem(user.username, JSON.stringify(user.job));
    setUsers([...users, { username: user.username, job: user.job }]);
    setUser({ username: "", job: "" });
  };

  useEffect(() => {
    const storedUsers = [];
    for (let i = 0; i < localStorage.length; i++) {
      const key = localStorage.key(i);
      storedUsers.push({ username: key, job: JSON.parse(localStorage.getItem(key)) });
    }
    setUsers(storedUsers);
  }, []);
 const removeuser = (user) => {
    localStorage.removeItem(user.username);
    setUsers(users.filter((u) => u.username !== user.username));
 }
  return (
    <>
      <div>Users</div>
      <label htmlFor="username">Username</label>
      <input type="text" id="username" name="username" value={user.username} onChange={handleChange} />
      <br />
      <label htmlFor="job">Job</label>
      <input type="text" id="job" name="job" value={user.job} onChange={handleChange} />
      <br />
      <button onClick={addUser}>Add User</button>
      
      <h2>List of Users</h2>
      <ul>
        {users.map((u, index) => (
          <li key={index}>
            {u.username} - {u.job}
            <button onClick={removeuser(u.username)}>remove</button>
          </li>
            
            ))}
      </ul>
    </>
  );
}
