import React, { useState, useEffect } from 'react';

export default function Users() {
  const [user, setUser] = useState({
    username: "",
    job: ""
  });
  const [task, setTask] = useState("");
  const [users, setUsers] = useState([]);

  const handleChange = (e) => { 
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
  };

  const handleTaskChange = (e) => {
    setTask(e.target.value);
  };

  const addUser = () => {
    localStorage.setItem(user.username, JSON.stringify({ ...user, tasks: [] }));
    setUsers([...users, { ...user, tasks: [] }]);
    setUser({ username: "", job: "" });
  };

  const addTask = (username) => {
    const userData = JSON.parse(localStorage.getItem(username));
    userData.tasks.push(task);
    localStorage.setItem(username, JSON.stringify(userData));
    setUsers(users.map(u => u.username === username ? { ...u, tasks: userData.tasks } : u));
    setTask("");
  };

  const removeUser = (username) => {
    localStorage.removeItem(username);
    setUsers(users.filter(u => u.username !== username));
  };

  const removeTask = (username, taskIndex) => {
    const userData = JSON.parse(localStorage.getItem(username));
    userData.tasks.splice(taskIndex, 1);
    localStorage.setItem(username, JSON.stringify(userData));
    setUsers(users.map(u => u.username === username ? { ...u, tasks: userData.tasks } : u));
  };

  useEffect(() => {
    const storedUsers = [];
    for (let i = 0; i < localStorage.length; i++) {
      const key = localStorage.key(i);
      storedUsers.push(JSON.parse(localStorage.getItem(key)));
    }
    setUsers(storedUsers);
  }, []);

  return (
    <>
      <div>Users</div>
      <label htmlFor="username">Username</label>
      <input type="text" id="username" name="username" value={user.username} onChange={handleChange} />
      <label htmlFor="job">Job</label>
      <input type="text" id="job" name="job" value={user.job} onChange={handleChange} />
      <button onClick={addUser}>Add User</button>
      
      <h2>List of Users</h2>
      <ul>
        {users.map((u, index) => (
          <li key={index}>
            {u.username} - {u.job}
            <button onClick={() => removeUser(u.username)}>Remove</button>
            <ul>
              {u.tasks.map((t, i) => (
                <li key={i}>
                  {t}
                  <button onClick={() => removeTask(u.username, i)}>Remove Task</button>
                </li>
              ))}
            </ul>
            <input type="text" value={task} onChange={handleTaskChange} placeholder={`Add task for ${u.username}`} />
            <button onClick={() => addTask(u.username)}>Add Task</button>
          </li>
        ))}
      </ul>
    </>
  );
}
