import React, { useState } from 'react';

export default function Task({ username, tasks, setUsers }) {
  const [task, setTask] = useState("");

  const handleTaskChange = (e) => {
    setTask(e.target.value);
  };

  const addTask = () => {
    const userData = JSON.parse(localStorage.getItem(username));
    userData.tasks.push(task);
    localStorage.setItem(username, JSON.stringify(userData));
    setUsers(prevUsers => prevUsers.map(u => u.username === username ? { ...u, tasks: userData.tasks } : u));
    setTask("");
  };

  const removeTask = (taskIndex) => {
    const userData = JSON.parse(localStorage.getItem(username));
    userData.tasks.splice(taskIndex, 1);
    localStorage.setItem(username, JSON.stringify(userData));
    setUsers(prevUsers => prevUsers.map(u => u.username === username ? { ...u, tasks: userData.tasks } : u));
  };

  return (
    <>
      <ul>
        {tasks.map((t, i) => (
          <li key={i}>
            {t}
            <button onClick={() => removeTask(i)}>Remove Task</button>
          </li>
        ))}
      </ul>
      <input type="text" value={task} onChange={handleTaskChange} placeholder={`Add task for ${username}`} />
      <button onClick={addTask}>Add Task</button>
    </>
  );
}
